import React from 'react';
import './login.css';
import {  withStyles,TextField, MenuItem, Paper, Button, Typography} from '@material-ui/core';


const styles = theme => ({
    paper: {
        marginTop: theme.spacing(8),
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
      },
      link: {
        display: 'flex',
      },
      icon: {
        marginRight: theme.spacing(0.5),
        width: 25,
        height: 25,
      },
});

export class NewTask extends React.Component {

    constructor(props){
        super(props);
        this.state = {description:'',responsible:'',state:'',date:''};
        this.handleDescription = this.handleDescription.bind(e);
        this.handleResponsible = this.handleResponsible.bind(e);
        this.handleState = this.handleState.bind(e);
        this.handleDate = this.handleDate.bind(e);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleDescription(e){
        this.setState({
            description: e.target.value
        });
    };

    handleResponsible(e){
        this.setState({
            responsible: e.target.value
        });
    };

    handleState(e){
        this.setState({
            state: e.target.value
        });
    };

    
    handleDate(){
        this.setState({
            date: new Date().getDate()+"/"+ new Date().getMonth() +"/"+ new Date().getFullYear()
        });
    };

    handleSubmit(e){
        e.preventDefault();
        console.log("vamos a logearnos");
        if(this.state.user === "andres@hotmail.com" && this.state.password === "3015"){
            localStorage.setItem('isLoggedIn',true);
            this.props.login();
        }else{
            localStorage.setItem('isLoggedIn',false);
        }
    };

    render() { 
        const { classes } = this.props;
                
        return (
            <div className="fondo">
                 <div>
                    <form onSubmit={this.handleSubmit} className="form" >
                        <br></br>
                        <h2>Iniciar Sesión</h2>
                        <br></br>
                        <div className="text">
                            <div>
                                <TextField  id="username" label="Username" type="email" placeholder="andres@hotmail.com"
                                    onChange={this.handleUserChange} fullWidth autoFocus required />
                            </div>
                            <br></br>
                            <div >
                                <TextField id="username" label="Password" type="password" placeholder="3015"
                                    onChange={this.handlePasswordChange} fullWidth required />
                            </div >
                        </div>
                        <br></br>
                        <br></br>
                        <br></br>
                        <div>
                            <Button type="submit" color="primary" variant="contained"  className="submit"
                                onChange={this.handleSubmit}>
                                Entrar    
                            </Button>
                        </div>
                        <br></br>
                        <br></br>
                    </form>
                </div>
            </div>
        );
    }
}

export default withStyles(styles)(NewTask);
