import React from 'react';
import './login.css';
import {  withStyles,TextField, MenuItem, FormControl, Button, InputLabel} from '@material-ui/core';


const styles = theme => ({
    paper: {
        marginTop: theme.spacing(8),
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
      },
      link: {
        display: 'flex',
      },
      icon: {
        marginRight: theme.spacing(0.5),
        width: 25,
        height: 25,
      },
      formControl: {
        margin: theme.spacing(1),
        minWidth: 120,
      },
      selectEmpty: {
        marginTop: theme.spacing(2),
      },
});

export class NewTask extends React.Component {

    constructor(props){
        super(props);
        this.state = {description:'',responsible:'',estado:'',date:''};
        this.handleDescriptionChange = this.handleDescriptionChange.bind(this);
        this.handleResponsibleChange = this.handleResponsibleChange.bind(this);
        this.handleEstadoChange = this.handleEstadoChange.bind(this);
        this.handleDateChange = this.handleDateChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleDescriptionChange(e){
        this.setState({
            description: e.target.value
        });
    };

    handleResponsibleChange(e){
        this.setState({
            responsible: e.target.value
        });
    };

    handleEstadoChange(e){
        this.setState({
            estado: e.target.value
        });
    };

    
    handleDateChange(){
        this.setState({
            date: new Date().getDate()+"/"+ new Date().getMonth() +"/"+ new Date().getFullYear()
        });
    };

    handleSubmit(e){
        e.preventDefault();
        console.log("vamos a registrar");
        if(this.state.description != "" && this.state.responsible != "" && this.state.state !="" 
            && this.state.date != ""){
                alert("datos correctos")
            
        }else{
            alert("datos incorrectos")
        }
    };

    render() { 
        const { classes } = this.props;
                
        return (
            <div className="fondo">
                 <div>
                    <form onSubmit={this.handleSubmit} className="form" >
                        <br></br>
                        <h2>Registrar</h2>
                        <br></br>
                        <div className="text">
                            <div>
                                <TextField  id="description" label="Description" type="email" placeholder="description"
                                    onChange={this.handleDescription} fullWidth autoFocus required />
                            </div>
                            <br></br>
                            <div >
                                <TextField id="responsible" label="responsible" type="password" placeholder="responsible"
                                    onChange={this.handleResponsible} fullWidth required />
                            </div >
                        </div>
                        <br></br>
                        <FormControl className={classes.formControl}>
                            <InputLabel id="demo-simple-select-label">Status</InputLabel>
                            <Select labelId="demo-simple-select-label"
                                id="demo-simple-select"
                                onChange={this.handleEstado}
                                >
                            <MenuItem value={10}>Ready</MenuItem>
                            <MenuItem value={20}>Completed</MenuItem>
                            <MenuItem value={30}>In Progress</MenuItem>
                            </Select>
                        </FormControl>
                        <br></br>
                        <div>
                            <Button type="submit" color="primary" variant="contained"  className="submit"
                                onChange={this.handleSubmit}>
                                Entrar    
                            </Button>
                        </div>
                        <br></br>
                        <br></br>
                    </form>
                </div>
            </div>
        );
    }
}

export default withStyles(styles)(NewTask);
