import React, {Component} from 'react';
import logo from './logo.svg';
import './App.css';
import {Login} from './components/login';
import {BrowserRouter as Router, Redirect, Route} from 'react-router-dom'
import {TempDrawer} from './components/tempDrawer';

class App extends Component {

    constructor(props) {
        console.log("estamos en app");
        super(props);

        this.state = {isLoggedIn: false};
        this.handleLogginChange = this.handleLogginChange.bind(this);
    }

    handleLogginChange() {
        this.setState({
            isLoggedIn: true
        });
    }

    render() {
        const LoginView = () => (<Login Login={this.handleLogginChange}/>);

        // const DrawerView = () => (<TempDrawer tempDrawer={this.handleDrawerChange}/>);

        return (
            <Router>
                <div className="App">
                    <header className="App-header">
                        <img src={logo} className="App-logo" alt="logo"/>
                        <h3 className="App-title">Lab 3 IETI</h3>
                    </header>
                    <div>
                        <h4 >user : andres</h4>
                        <h4 >pass: 3015</h4>
                        {this.state.isLoggedIn ? <Redirect to="/panel" />: <Redirect to="/"/>}
                        <Route exact path="/" component={LoginView}/>
                        <Route exact path="/panel" component={LoginView}/>
                    </div>
                </div>
            </Router>
        );
    }
}
export default App;
